export default async function main(args) {
  // Send a custom video response on the Voiceflow webchat by using the video extension
  // available at https://github.com/voiceflow-gallagan/vf-extensions-demo

  // Sample video URL from YouTube - replace with a relevant video for your use case
  const video = "https://www.youtube.com/watch?v=dQw4w9WgXcQ";

  // Constructing the custom video trace
  return {
    next: { path: 'success' }, // Indicating the successful execution of the function
    trace: [
      {
        type: "ext_video", // Custom trace type for video content
        payload: {
          videoUrl: video, // The URL of the video to be played
          autoplay: true, // Optional: Set video to autoplay
          controls: true // Optional: Enable video controls
        }
      }
    ] 
  };
}
